package operacoes_logicas;

public class Aula06_FOR {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int ct = 0, ct2 = 0;
			for(ct = 0; ct < 10; ct ++) {
			System.out.println("\nO numero do For é "+ct);			
		}// for 
		
		for(ct2 = 10; ct2 >=0; ct2--) {
			System.out.println("\nO Valor de CT2 é "+ ct2);
			
		}// fim do for 
	}

}
