package aula01Fundamentos;

public class Break {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 0;
		while (i < 10) {
			System.out.println("o valor de  i é "+ i);
			i++;
			if (i > 5) break; {
				
			}// fim do if
					
		}
		System.out.println("fim do break");

	}

}
