package aula01Fundamentos;

public class While { // ciclo while

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int cont = 0;
		
	while(cont < 20) {
		cont++;
		System.out.println("O Conte tem valor de " + cont);
		
		
	}// fim while	
	int n=0;
	while(n!=10) {
		System.out.println(n);
		n++;
		
		
	}

	}
	

}
