package aula02;

public class ArrayOuMatrizOuBidimencional {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a [][] = new int [4][5]; // 4 linhas e 5 coluna
		a[0][0]=1;a[0][1]=2;a[0][2]=3;a[0][3]=4;a[0][4] = 5;
		
		
		
		System.out.println(" a[0][0] "+ a[0][0] );
		System.out.println(" a[0][1] "+ a[0][1] );
		System.out.println(" a[0][2] "+ a[0][2] );
		System.out.println(" a[0][3] "+ a[0][3] );
		System.out.println(" a[0][4] "+ a[0][4] );
		System.out.println(" O comprimento da Matriz  "+ a.length );
		
		

	}

}
